document.addEventListener("DOMContentLoaded",function(){const elements=document.querySelectorAll("[data-Qy-r]");const observer=new IntersectionObserver((entries)=>{entries.forEach((entry)=>{if(entry.isIntersecting){entry.target.classList.add("active")}})},{threshold:0.1});elements.forEach((element)=>{observer.observe(element)})});document.addEventListener("DOMContentLoaded",function(){const elements=document.querySelectorAll("[data-Qy-rb]");const observer=new IntersectionObserver((entries)=>{entries.forEach((entry)=>{if(entry.isIntersecting&&!entry.target.classList.contains("active")){setTimeout(()=>{entry.target.classList.add("active")},100)}else if(!entry.isIntersecting&&entry.boundingClientRect.top>0){setTimeout(()=>{entry.target.classList.remove("active")},100)}})},{threshold:0.2});elements.forEach((element)=>{observer.observe(element)})})