document.addEventListener('DOMContentLoaded',function(){const elements=document.querySelectorAll('[data-Qy-r]');const observer=new IntersectionObserver((entries)=>{entries.forEach((entry)=>{if(entry.isIntersecting){entry.target.classList.add('active')}})},{threshold:0.1});elements.forEach((element)=>{observer.observe(element)})});document.addEventListener('DOMContentLoaded',function(){const elements=document.querySelectorAll('[data-Qy-rb]');const observer=new IntersectionObserver((entries)=>{entries.forEach((entry)=>{if(entry.isIntersecting){entry.target.classList.add('active')}else{entry.target.classList.remove('active')}})},{threshold:0.1});elements.forEach((element)=>{observer.observe(element)})})